import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faCoffee, faShoppingCart, faTimes } from "@fortawesome/free-solid-svg-icons";

import "../styles/NewNavbar.css"

const NewNavbar = () => {
  const [clicked, setClicked] = useState(false);

  const toggleSidebar = () => {
    const handleClick = () => setClicked(!clicked)

    if (clicked) {
      document.querySelector(".sidebar").classList.add("open");
      handleClick();
    } else {
      document.querySelector(".sidebar").classList.remove("open")
      handleClick();
    }
  }

  // useEffect currently for debugging only.
  useEffect(() => console.log(clicked), [clicked])

  // Note to self. The className will pick up ANY css with the same name.
  return (
    <React.Fragment>
      <nav className="navbar">
        <div className="navbar-container">
          <div className="nav-icon" onClick={toggleSidebar}>
            <FontAwesomeIcon icon={clicked ? faTimes : faBars} />
          </div>
          <div className="nav-icon">
            <Link to="/">
              <FontAwesomeIcon icon={faCoffee} />
            </Link>
          </div>
          <div className="nav-icon">
            <Link to="/cart">
              <FontAwesomeIcon icon={faShoppingCart} />
            </Link>
          </div>
        </div>
      </nav>
      <aside className="sidebar">
        <h3>Pages</h3>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/store">Store</Link>
          </li>
        </ul>
      </aside>
    </React.Fragment>
  );
};

export default NewNavbar;
